<script>
export default {
  name: "finsh-project",
  props: {
    data: {
      type: Object,
      default: null,
    },
  },
  computed: {
    bgColor() {
      let value = 'red';
      if (this.data) {
        value = this.data.style.bgColor;
      }
      return value;
    },
    TimeStr() {
      let value = 'red';
      if (this.data) {
        let start = this.data.data.startTime.split(" ")[0];
        let end = this.data.data.endTime.split(" ")[0];
        value = start.slice(5, 7) + "月" + start.slice(8, 10) + "日" + "-" + end.slice(5, 7) + "月" + end.slice(8, 10) + "日";
      }
      return value;
    }
  }
};
</script>

